def insert_cover_image():
    # import pdb;pdb.set_trace()
    edu_count = 0
    for i in range(1, 206):
        domain = NGO.objects.get(pk=i).work_domain
        if domain == 'education':
            img_url = '/media/ngo/' + domain + str(edu_count % 10) + '.jpg'
